let functions = {
    generateRandomGuid: function generateRandomGuid() {
        // Generate a random GUID
        return 'xxxxxxxx-xxxx-4xxx-yxxx-xxxxxxxxxxxx'.replace(/[xy]/g, function (c) {
            var r = Math.random() * 16 | 0, v = c === 'x' ? r : (r & 0x3 | 0x8);
            return v.toString(16);
        });
    },
    generateEmptyCalendar: function generateEmptyCalendar(accountId) {
        return {
            calendarId: flow.get("functions", "file").generateRandomGuid(),
            calendarAdmin: accountId,
            days: []
        }
    },
    generateDaySlots: function generateDaySlots(day, config) {
        const { startTime, endTime, slotDuration, pauseDuration, timezone } = config;

        // Create a new Date object for the start time of the specific day
        let currentStartTime = moment.tz(`${day} ${startTime}`, timezone);
        let currentEndTime = moment.tz(`${day} ${endTime}`, timezone);
        // Initialize an array to hold the slots for the day
        let slots = [];

        // Loop to create time slots for the specific day
        while (currentStartTime < currentEndTime) {
            // Add the current slot to the slots array
            slots.push({
                id: flow.get("functions", "file").generateRandomGuid(), // Generate a random GUID for the slot ID
                start: parseInt(moment(currentStartTime).format('x')), // Convert to epoch time in milliseconds
                end: parseInt(moment(currentStartTime).add(slotDuration, 'minutes').format('x')), // Convert to epoch time in milliseconds
                status: "free",
                enabled: false,
                paymentUrl: "",
                bookingInfo: {}
            });

            // Increment the current start time by the slot duration and the pause duration
            currentStartTime = moment(currentStartTime).add(slotDuration + pauseDuration, 'minutes');
        }

        // Return the day object with the slots
        return {
            day: day,
            slots: slots
        };
    },
    addDaySlots: function addDaySlots(calendar, day, config) {
        // Check if the day is already in the calendar
        const existingDay = calendar.days.find(d => d.day === day);
        if (existingDay) {
            throw new Error("Day already exists in the calendar.");
        }

        // Generate slots for the specific day
        const newDaySlots = flow.get("functions", "file").generateDaySlots(day, config);

        // Add the new day with its slots to the calendar's days array
        calendar.days.push(newDaySlots);
        return calendar.days.find(d => d.day === day);
    },
    getDefaultSlotConfig: function getDefaultSlotConfig() {
        var slotConfig = flow.get("slotConfig","file");
        if (typeof slotConfig === 'object' &&
            slotConfig.hasOwnProperty('startTime') &&
            slotConfig.hasOwnProperty('endTime') &&
            slotConfig.hasOwnProperty('slotDuration') &&
            slotConfig.hasOwnProperty('pauseDuration') &&
            slotConfig.hasOwnProperty('timezone')) {
                return slotConfig;
            } else {
                return {
                    startTime: "09:00",
                    endTime: "15:00",
                    slotDuration: 10,
                    pauseDuration: 5,
                    timezone: 'Europe/Zurich'
                }
            }
    }
}

flow.set("functions", functions, "file");

